mapper.update:
UPDATE companysales_tb
SET comId = #{companyId}, daysales = #{sales}
WHERE salesId = #{salesId}

mapper.select:
SELECT salesId,comId,daysales,regdate
FROM companysales_tb
WHERE comId = #{companyId}

SELECT salesId,comId,daysales,regdate
FROM companysales_tb
WHERE salesId = #{salesId}
