mapper.update:
UPDATE review_tb
SET
content = #{content}
WHERE reviewId = #{reviewId}

mapper.select:
SELECT reviewId, userId, comId, content, regDate, reported FROM review_tb
WHERE comId = #{comId}
ORDER BY regDate asc

mapper.update:
UPDATE review_tb
SET
reported = ${reported=='0'?"'N'":"'Y'"}
WHERE reviewId = #{reviewId}
